mapper.delete:
UPDATE user
SET deleted_at = now()
WHERE id = #{id}
  AND deleted_at IS NULL;

mapper.select:
SELECT id, email, name, gender, phone_number, address, is_active, birthdate, created_at, updated_at, deleted_at FROM user WHERE id = #{id}
AND deleted_at IS
NULL NOT NULL

SELECT id, email, name, gender, phone_number, address, is_active, birthdate, created_at, updated_at, deleted_at
FROM user

  AND email = #{email}
  AND phone_number = #{phoneNumber}
  AND name = #{name}
  AND birth_date = #{birthDate}

    AND deleted_at IS

      NULL
      NOT NULL

mapper.update:
UPDATE user
SET deleted_at = NULL
WHERE id = #{id}
  AND deleted_at IS NOT NULL;

UPDATE user
SET password = #{password}
WHERE email = #{email}
  AND deleted_at IS NULL;
